import React, { useState, useEffect } from "react";
import axios from "axios";

export default function LoadModel() {
  const [adapters, setAdapters] = useState([]);
  const [selected, setSelected] = useState("");
  const [message, setMessage] = useState("");
  const [reply, setReply] = useState("");

  useEffect(() => {
    axios.get('/api/adapters').then(res => setAdapters(res.data));
  }, []);

  const fuse = () => {
    axios.post('/api/fuse', { adapter: selected }).then(() => alert("Model fused!"));
  };

  const chat = () => {
    axios.post('/api/chat', { prompt: message })
      .then(res => setReply(res.data.response))
      .catch(err => console.error(err));
  };


  return (
    <div>
      <h2>Load & Chat</h2>
      <select onChange={(e) => setSelected(e.target.value)}>
        {adapters.map((a, i) => <option key={i}>{a}</option>)}
      </select>
      <button onClick={fuse}>Fuse Model</button>

      <h3>Chat</h3>
      <input value={message} onChange={(e) => setMessage(e.target.value)} />
      <button onClick={chat}>Send</button>
      <p>Response: {reply}</p>
    </div>
  );
}
